import React, { Component } from "react";
import firebase from "firebase/compat/app";
import firebaseConfig from "../assets/authConfig/authConfig.js";
import logo from "../assets/img/logo.jpeg";
import "firebase/compat/auth";
import "../assets/style/style.css";

const app = firebase.initializeApp(firebaseConfig);
class CreateUser extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  registraUsuario(e) {
    e.preventDefault();
    let usuario = document.getElementById("usuario").value;
    let contrasena = document.getElementById("password").value;

    app
      .auth()
      .createUserWithEmailAndPassword(usuario, contrasena)
      .then(() => {
        document.getElementById("alert").className =
          "alert alert-success visible";
        document.getElementById("alert").innerHTML =
          "Usuario creado exitosamente";
      })
      .catch((err) => {
        document.getElementById("alert").className =
          "alert alert-danger visible";
        document.getElementById("alert").innerHTML = "Usuario existente";
      });
  }

  render() {
    return (
      <>
        <div className="container">
          <img className="rounded mx-auto d-block" src={logo} alt="" />
          <div
            className="alert alert-danger invisible"
            id="alert"
            role="alert"
          ></div>
          <div className="login">
            <form onSubmit={this.registraUsuario.bind(this)}>
              <label className="control-label" htmlFor="">
                E-mail
              </label>
              <input className="form-control" type="email" id="usuario" />
              <label className="control-label" htmlFor="">
                Contrase√±a
              </label>
              <input className="form-control" type="password" id="password" />
              <input className="btn btn-primary" type="submit" />
            </form>
          </div>
        </div>
      </>
    );
  }
}
export default CreateUser;
