import React, { Component } from 'react';
import firebase from 'firebase/compat/app';
import firebaseConfig from './authConfig/authConfig.js'
import 'firebase/compat/auth';
import './style/style.css';

const app = firebase.initializeApp(firebaseConfig);
class CreateUser extends Component {
    constructor(props) {
        super(props);
        this.state = {}
    }

    registraUsuario(e) {
        e.preventDefault()
        let usuario = document.getElementById("usuario").value;
        let contrasena = document.getElementById("password").value;

        app.auth().createUserWithEmailAndPassword(usuario, contrasena)
            .then(() => {
                document.getElementById("alert").textContent ="Usuario creado exisoamente"
                .className="alert alert-success visible";
            }).catch((err) => {
                document.getElementById("alert").append
                .className="alert alert-danger visible";
            })
    }

    render() {
        return (
            <>
                <div className="alert alert-danger invisible" id="alert" role="alert"></div>
                <form onSubmit={this.registraUsuario.bind(this)}>
                    <label className="control-label" htmlFor="">E-mail</label>
                    <input className="form-control" type="email" id="usuario" />
                    <label className="control-label" htmlFor="">Contrase√±a</label>
                    <input className="form-control" type="password" id="password" />
                    <input className="btn btn-primary" type="submit" />
                </form>
            </>
        );
    }
}
export default CreateUser;
