import React, { Component } from "react";
import firebase from "firebase/compat/app";
import firebaseConfig from "./authConfig/authConfig.js";
import "firebase/compat/auth";

const app = firebase.initializeApp(firebaseConfig);
class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  registraUsuario(e) {
    e.preventDefault();
    let usuario = document.getElementById("usuario").value;
    let contrasena = document.getElementById("password").value;

    app
      .auth()
      .createUserWithEmailAndPassword(usuario, contrasena)
      .then(() => {
        alert("User created");
      })
      .catch((err) => {
        alert("Usuario ya existente");
      });
  }

  render() {
    return (
      <>
        <div class="alert alert-danger" role="alert">
          A simple danger alertâ€”check it out!
        </div>
        <form onSubmit={this.registraUsuario.bind(this)}>
          <label className="control-label" htmlFor="">
            Nombre
          </label>
          <input className="form-control" type="text" id="usuario" />
          <label className="control-label" htmlFor="">
            Contrasena
          </label>
          <input className="form-control" type="password" id="password" />
          <input className="btn btn-primary" type="submit" />
        </form>
      </>
    );
  }
}
export default Login;
